// GaugeWidget
        function setGaugeWidget(gaugeConfig)
        {
            var degrees = 180.0 * (gaugeConfig.hihi - gaugeConfig.value) / (gaugeConfig.hihi - gaugeConfig.lolo);
            if (degrees < 0.0 )   degrees = 0.0;
            if (degrees > 180.0 ) degrees = 180.0;
            // Trig to calc meter point
            var radius = 0.50;
            var radians = degrees * Math.PI / 180;
            var x = radius * Math.cos(radians);
            var y = radius * Math.sin(radians);
            var path1 = (degrees < 45 || degrees > 135) ? 'M -0.0 -0.025 L 0.0 0.025 L ' : 'M -0.025 -0.0 L 0.025 0.0 L ';
            // Path: may have to change to create a better triangle
            var mainPath = path1,
                pathX = String(x),
                space = ' ',
                pathY = String(y),
                pathEnd = ' Z';
            var rotation = (180.0 * (gaugeConfig.high - gaugeConfig.lolo) / (gaugeConfig.hihi - gaugeConfig.lolo)) - 90.0;
            var path = mainPath.concat(pathX,space,pathY,pathEnd);

            var valueColor = gaugeConfig.backgroundColor; 

            if ( (gaugeConfig.lolo <= gaugeConfig.value) && (gaugeConfig.value < gaugeConfig.low) )
            {
                valueColor = gaugeConfig.lowColor;
            }
            if ( (gaugeConfig.low  <= gaugeConfig.value) && (gaugeConfig.value < gaugeConfig.high) )
            {
                valueColor = gaugeConfig.mediumColor;
            }
            if ( (gaugeConfig.high <= gaugeConfig.value) && (gaugeConfig.value < gaugeConfig.hihi) )
            {
              valueColor = gaugeConfig.highColor;
            }
            if ( gaugeConfig.hihi <= gaugeConfig.value)
            {
                valueColor = gaugeConfig.highColor;
            }
            valueColor = gaugeConfig.titleColor;

            var data = 
                [
                    { 
                        type: 'scatter',
                        x: [0], y:[0],
                        marker: {size: 14, color:valueColor},
                        showlegend: false,
                        name: gaugeConfig.label,
                        text: gaugeConfig.value.toString(),
                        hoverinfo: 'text+name',


                    },
                    { 
                        values: 
                            [
                                gaugeConfig.hihi - gaugeConfig.high, 
                                gaugeConfig.high - gaugeConfig.low, 
                                gaugeConfig.low  - gaugeConfig.lolo, 
                                gaugeConfig.hihi - gaugeConfig.lolo 
                            ],
                        rotation: rotation,
                        text: 
                            [
                                gaugeConfig.high.toString() + '&#8596;' + gaugeConfig.hihi.toString(), 
                                gaugeConfig.low.toString()  + '&#8596;' + gaugeConfig.high.toString(), 
                                gaugeConfig.lolo.toString() + '&#8596;' + gaugeConfig.low.toString(), 
                                ''
                            ],
                        textinfo: 'text',
                        textposition:'inside',
                        textfont: {color: gaugeConfig.labelColor, size:gaugeConfig.numberSize},
                        marker: 
                            {
                                colors:
                                    [
                                        gaugeConfig.highColor,
                                        gaugeConfig.mediumColor,
                                        gaugeConfig.lowColor, 
                                        gaugeConfig.backgroundColor
                                    ]
                            },
                        hoverinfo: 'label',
                        hole: 0.5,
                        type: 'pie',
                        showlegend: false,
                        sort: false
                    }
                ];
            var layout = 
                {
                    shapes:
                        [
                            {
                                type: 'path',
                                path: path,
                                fillcolor: valueColor,
                                line: {color: valueColor }
                            }
                        ],
                    height: gaugeConfig.size,
                    width: gaugeConfig.size,
                    title: 
                        {
                            text:gaugeConfig.label + ' ' + gaugeConfig.value.toString() + ' ' + gaugeConfig.unit,
                            font: {color: valueColor, size:gaugeConfig.labelSize},
                         },
                    xaxis: 
                        {
                            zeroline:false, 
                            showticklabels:false,
                            showgrid: false, 
                            range: [-1, 1],
                        },
                    yaxis: 
                        {
                            zeroline:false, 
                            showticklabels:false,
                            showgrid: false, 
                            range: [-1, 1]
                        },
                    margin: 
                        {
                            t: 80, //top margin
                            l: 0, //left margin
                            r: 0, //right margin
                            b: 20 //bottom margin
                        },
                    plot_bgcolor:gaugeConfig.backgroundColor,
                    paper_bgcolor:gaugeConfig.backgroundColor

                };
            $('#' + gaugeConfig.htmlId).attr('style','max-height: calc(' + (gaugeConfig.size / 2).toString() + 'px + 50px) !important; overflow:hidden;');
            Plotly.newPlot(gaugeConfig.htmlId, data, layout, {'displayModeBar': false});
        }

